class MemberIndex extends NodeIndex {

  constructor(doc) {
    super();

    this.doc = doc;
    this.index = new TreeIndex();
  }

  /**
    Selects all nodes which have a parent.

    @param {Node}
    @returns {Boolean} true if the given node should be added to the index.
   */
  select(node) {
    return node.hasParent()
  }

  get(...args) {
    return this.index.get(...args)
  }

  _getPath(node, parentId) {
    var doc = this.doc;
    parentId = parentId || node.parent;
    var type = node.type;
    var parent = doc.get(parentId);

    if (!parent) {
      console.error('Could not retrieve parent node of member.');
      return [parentId, type, node.name]
    }

    if (parent.type === "class") {
      if (node.isStatic || type === "ctor") {
        return [parentId, 'class', type, node.name]
      } else {
        return [parentId, 'instance', type, node.name]
      }
    } else {
      return [parentId, type, node.name]
    }
  }

  /**
    Called when a node has been created.

    @param {Node} node
   */
  create(node) {
    this.index.set(this._getPath(node), node);
  }

  /**
    Called when a node has been deleted.

    @param {Node} node
   */
  delete(node) {
    this.index.delete(this._getPath(node));
  }

  /**
    Called when a property has been updated.

    @param {Node} node
   */
  update(node, path, newValue, oldValue) {
    if (!this.select(node) || path[1] !== 'parent') return
    this.index.delete(this._getPath(node, oldValue));
    this.index.set(this._getPath(node, newValue), node);
  }

  clone() {
    return new MemberIndex(this.doc)
  }

  _clear() {
    this.index = new TreeIndex();
  }

}

function getBasename(p) {
  let idx = p.lastIndexOf('/');
  if (idx > -1) {
    return p.slice(idx+1)
  } else {
    return p
  }
}

const CLASS_PREFIX = /^([^.#]+)([.#])([^.#]+)/;
const DOT = '.'.charCodeAt(0);

class LookupTable extends NodeIndex {

  constructor(doc) {
    super();
    this.doc = doc;
    this.table = new TreeIndex.Arrays();
  }

  select(node) {
    return node.isAPINode
  }

  /*
    Classes:
      - fully-qualified: `ui/Component`
      - short: `Component`
    Instance Methods:
      - fully-qualified: ui/Component#foo()
      - short: Component#foo
    Instance Properties:
      - fully-qualified: ui/Component#baz
      - short: Component#baz
      - alias: if no name clash it is fine to register it as Component.bar
    Static Methods/Properties:
      - same but with '.'
  */
  create(node) {
    // get all aliases for which we want this node be registered
    let base = getBasename(node.id);
    switch (node.type) {
      case 'class':
      case 'module':
        this.table.add(base, node.id);
        break
      default:
        //
    }
  }

  delete(node) {
    let base = getBasename(node.id);
    this.table.remove(base, node.id);
  }

  clone() {
    return new LookupTable(this.doc)
  }

  lookupId(id, hints) {
    const doc = this.doc;
    // the simplest case: id is fully qualified
    if (doc.contains(id)) {
      return id
    }
    // next we try use the short name
    // and see if it leads to something
    // e.g. 'Component' instead of 'ui/Component'
    const base = getBasename(id);
    let candidates = this.table.get(base) || [];
    if (candidates.length === 1) {
      return candidates[0]
    } else if (candidates.length > 2) {
      return this._disambiguate(id, candidates, hints)
    }
    // now we look it is a composed id, e.g. Foo.bar
    let m;
    if (candidates.length === 0) {
      m = CLASS_PREFIX.exec(id);
      if (m) {
        const parentId = this.lookupId(m[1]);
        const parent = parentId ? doc.get(parentId) : null;
        return this._lookupProp(parent, m[2], m[3])
      }
    }
    // giving up
    return ''
  }

  _lookupProp(parent, sep, name) {
    const doc = this.doc;
    // try the given one first
    let id = parent.id+sep+name;
    if (doc.contains(id)) return id
    // Maybe tried to access an instance prop using DOT notation
    if (parent.type === 'class' && sep.charCodeAt(0) === DOT) {
      id = parent.id+'#'+name;
      if (doc.contains(id)) return id
    }
    // last we try to call that again but with '()''
    if (!/\(\)$/.exec(id)) {
      return this._lookupProp(parent, sep, name+'()')
    }
    return ''
  }

  _disambiguate(id, candidates, hints) { // eslint-disable-line
    // TODO: when we face a situation with name clashes
    // then we need to use the hints somehow, to disambiguate in a meaningful way
    console.warn('Multiple classes or modules exist with the same simple name', getBasename(id));
    return candidates[0]
  }

  _clear() {
    this.table = new TreeIndex.Arrays();
  }

}

